import maya.cmds as cmds
import base64
import os

image_library = {
    'menu_icon_maya_to_discord': '',
    'menu_icon_fspy': '',
    'biped_rigger_proxy_btn': '',
    'biped_rigger_rig_btn': '',
                }


def gt_generate_icons():
    """ Creates Menu Icons for GT-Tools """
    icons_folder_dir = cmds.internalVar(userBitmapsDir=True) 
    
    # GT Maya to Discord Icon
    gt_mtod_icon_image = icons_folder_dir + 'gt_maya_to_discord_icon.png'
    gt_fspy_icon_image = icons_folder_dir + 'gt_fspy_importer.png'

    if os.path.isdir(icons_folder_dir):
        # Maya to Discord
        generate_image(gt_mtod_icon_image, image_library.get('menu_icon_maya_to_discord'))
        
        # fSpy Importer
        generate_image(gt_fspy_icon_image, image_library.get('menu_icon_fspy'))


def generate_image(file_path, image_encoded):
    """
    Converts base64 image (provided as a string) into an actual file
    Args:
        file_path: Path to where the function should save the image (must be valid)
                   e,g, "C:\\image.png"
        image_encoded (string): Base64 string containing an image

    Returns:
        Path to the generated image or None in case it fails
    """
    try:
        image_64_decode = base64.b64decode(image_encoded)
        image_result = open(file_path, 'wb')
        image_result.write(image_64_decode)
        image_result.close()
    except Exception as e:
        print("Failed to generate image: ", e)
        return None

    if os.path.exists(file_path):
        return file_path
    else:
        return None


def generate_image_from_library(file_path, image_key):
    """
    Creates an image using the data from the "image_library"
    Args:
        file_path: Path to where the function should save the image (must be valid)
                   e,g, "C:\\image.png"
        image_key: Key for a known image from the dictionary "image_library" found above

    Returns:
        The result of the "generate_image" function
    """
    if image_key in image_library:
        return generate_image(file_path, image_library.get(image_key))
    else:
        print("Image key not found.")
        return None


# Generate Icons Without Imports
if __name__ == '__main__':
    gt_generate_icons()
